sys_variable_value.value:
(function execute(inputs, outputs) {

    var taskGr = new GlideRecord(inputs.task_record.sys_class_name);
    taskGr.addQuery('sys_id', inputs.task_record.sys_id);
    taskGr.setLimit(1);
    taskGr.query();
    if (taskGr.next()) {
        var values = new global.scp_vars_Util().getItemValues(taskGr, inputs.catalog_item_sysid, inputs.encoded_values);
        var needsUpdate = false;

        for (var attr in values) {
            taskGr.variables[attr] = values[attr];
            needsUpdate = true;
        }

        if (needsUpdate) {
            try {
                taskGr.update();
                outputs.status = 'success';
            } catch (e) {
                outputs.status = 'error';
                outputs.message = String(e);
            }
        }
    } else {
        outputs.status = 'error';
        outputs.message = 'Failed to lookup RITM/SCTASK with Sys ID = ' + inputs.task_record.sys_id;
    }

})(inputs, outputs);

